mapper.select:
select count(1) as mun, count(case when jc.SFDJ='是' then 1 else null end) as djlr, count(case when jc.SFKC='是' then 1 else null end) as kclr, count(case when jc.SFXMSF='是' then 1 else null end) as xmsf, count(case when jc.SFTYJR='是' then 1 else null end) as twjr, count(case when jc.SFDB='是' then 1 else null end) as dbry, count(case when jc.SFCJ='是' then 1 else null end) as cjlr FROM (SELECT * FROM [dbo].[ZHSQ_JM]
and XQID = #{xqid} and SQID = #{sqid}
and (SFDJ like '%是%' or SFKC like '%是%' or SFXMSF like '%是%' or SFTYJR like '%是%' or SFDB like '%是%' or SFCJ like '%是%')) as jc

SELECT
TOP 5
LDMC,
count(LDID) as MUN
FROM
(SELECT ld.LDID,ld.LDMC FROM [dbo].[ZHSQ_JM] jm join [dbo].[ZHSQ_LD] ld on jm.LDID=ld.LDID

     and ld.XQID = #{xqid}
     and ld.SQID = #{sqid}
     and jm.SFDJ = #{sfdj}
     and jm.SFKC = #{sfkc}
     and jm.SFXMSF = #{sfxmsf}
     and jm.SFTYJR = #{sftyjr}
     and jm.SFDB = #{sfdb}
     and jm.SFCJ = #{sfcj}

and (jm.[SFDJ] = N'是' OR jm.[SFKC] = N'是' OR jm.[SFXMSF] = N'是' OR jm.[SFTYJR] = N'是' OR jm.[SFDB] = N'是' OR jm.[SFCJ] = N'是')) as a
 GROUP BY LDMC ORDER BY MUN DESC

SELECT
count(case when jc.[NL]  <  N'20' then 1 else null end) as a,
count(case when jc.[NL]  >=  N'20' and jc.[NL]  <=  N'30' then 1 else null end) as b,
count(case when jc.[NL]  >=  N'31' and jc.[NL]  <=  N'40' then 1 else null end) as c,
count(case when jc.[NL]  >=  N'41' and jc.[NL]  <=  N'50' then 1 else null end) as d,
count(case when jc.[NL]  >=  N'51' and jc.[NL]  <=  N'60' then 1 else null end) as e,
count(case when jc.[NL]  >  N'60' then 1 else null end) as f
FROM
(SELECT * FROM [dbo].[ZHSQ_JM]

     and XQID = #{xqid}
     and SQID = #{sqid}
     and SFDJ = #{sfdj}
     and SFKC = #{sfkc}
     and SFXMSF = #{sfxmsf}
     and SFTYJR = #{sftyjr}
     and SFDB = #{sfdb}
     and SFCJ = #{sfcj}

and ([SFDJ] = N'是' OR [SFKC] = N'是' OR [SFXMSF] = N'是' OR [SFTYJR] = N'是' OR [SFDB] = N'是' OR [SFCJ] = N'是')) as jc

SELECT XB ,count(1) as MUN FROM [dbo].[ZHSQ_JM]

     and XQID = #{xqid}
     and SQID = #{sqid}
     and SFDJ = #{sfdj}
     and SFKC = #{sfkc}
     and SFXMSF = #{sfxmsf}
     and SFTYJR = #{sftyjr}
     and SFDB = #{sfdb}
     and SFCJ = #{sfcj}

and ([SFDJ] = N'是' OR [SFKC] = N'是' OR [SFXMSF] = N'是' OR [SFTYJR] = N'是' OR [SFDB] = N'是' OR [SFCJ] = N'是')
GROUP BY XB

